import React, { FC } from 'react';
import { View, Text } from 'react-native';
import { Picker } from "@react-native-picker/picker";


interface CitySectorAndSectorSelectProps {
  citiesSector: { id: string; name: string }[];
  sectors: { id: string; name: string }[];
  selectedCitySectorId: string;
  selectedSectorId: string;
  onCitySectorChange: (citySectorId: string, citySectorName: string) => void;
  onSectorChange: (sectorId: string, sectorName: string) => void;
}

const CitySectorAndSectorSelect: FC<CitySectorAndSectorSelectProps> = ({ 
  citiesSector, sectors, selectedCitySectorId, selectedSectorId, onCitySectorChange, onSectorChange 
}) => {
  const handleCitySectorChange = (itemValue: string, itemIndex: number) => {
      const citySectorId = itemValue;
      const citySectorName = citiesSector.find(citySector => citySector.id === citySectorId)?.name;
      console.log('CITY ID : ',  citySectorId)
      console.log('CITY NAME : ', citySectorName)
      onCitySectorChange(citySectorId, citySectorName);
  };


  const handleSectorChange = (itemValue: string, itemIndex: number) => {
    const sectorId = itemValue;
    const sectorName = sectors.find(sector => sector.id === sectorId)?.name;
    onSectorChange(sectorId, sectorName);
  };
  

  return (
    <View style={styles.selectCitySectorSector}>
      <Text style={styles.text}>Sélectionner la ville : </Text>
      <Picker
        selectedValue={selectedCitySectorId}
        onValueChange={handleCitySectorChange}
      >
        <Picker.Item label="Choisir une ville" value="" />
        {citiesSector.map((citySector) => (
          <Picker.Item key={citySector.id} label={citySector.name} value={citySector.id} />
        ))}
      </Picker>

      {sectors.length > 0 && selectedCitySectorId && (
        <View>
          <Text>Sélectionner le secteur : </Text>
          <Picker
            selectedValue={selectedSectorId}
            onValueChange={handleSectorChange}
          >
            <Picker.Item label="Choisir un secteur" value="" />
            {sectors.map((sector) => (
              <Picker.Item key={sector?.id} label={sector?.name} value={sector?.id} />
            ))}
          </Picker>
        </View>
      )}
    </View>
  );
};

const styles = {
  selectCitySectorSector : {
    backgroundColor: 'white',
    padding: 15,
    marginBottom: 20,
    // marginBottom: 20,
    marginLeft: 10,
    marginRight: 10,
    borderRadius: 2
  },

}

export default CitySectorAndSectorSelect;
